# set the sortOrder of updatedLabel to 10, or adds this label to the question label list

curl --request POST \
  --url https://www.wixapis.com/faq/v1/questions/update-label-order \
  --header 'Authorization: <AUTH>' \
  --header 'Content-Type: application/json' \
  --data '{
  "updates": [
    {
      "question_id": "c166ddb4-a9b4-444b-8ef0-5960d2ca1a1c",
      "label": {
        "title": "updatedlabel",
        "sortOrder": 10
      }
    }
  ]
}'

curl --request POST \
  --url https://www.wixapis.com/faq/v1/question-entries \
  --header 'Authorization: <AUTH>' \
  --header 'Content-Type: application/json' \
  --data '{
	  "questionEntry": {
    "labels": [
      {
        "title": "testlabel",
        "sortOrder": 10
      },
      {
        "title": "differentlabel",
        "sortOrder": 10
      }
    ],
    "question": "What is the question?",
    "answer": {
      "draftjs": "{text: \"This is the answer\"}"
    },
    "status": "VISIBLE",
    "category_id": "d2c57684-c957-4d3b-b555-1157b1684616"
  }
}'

