curl -X POST \
  https://www.wixapis.com/stores/v1/subscription-options/createBulk \
  --data-binary '{
    "subscriptionOptions": [
      {
        "title": "Monthly auto-renewal",
        "subscriptionSettings": {
          "frequency": "MONTH",
          "autoRenewal": true
        }
      },
      {
        "title": "weekly (for 1 month)",
        "description": "save 10%",
        "subscriptionSettings": {
          "frequency": "WEEK",
          "billingCycles": 4
        },
        "discount": {
          "type": "PERCENT",
          "value": 10
        }
      }
    ]
    }'\
  -H 'Content-Type: application/json' \
  -H 'Authorization: <AUTH>'

